import { useState, useEffect } from 'react';

interface Todo {
  _id: string;
  title: string;
  completed: boolean;
}

export default function Home() {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [title, setTitle] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    setLoading(true);
    try {
      const response = await fetch(process.env.NEXT_PUBLIC_BACKEND_URL + '/api/todos');
      const data = await response.json();
      setTodos(data);
    } catch (error) {
      setError('Failed to load todos');
    } finally {
      setLoading(false);
    }
  };

  const addTodo = async () => {
    if (!title) return;
    setLoading(true);
    try {
      const response = await fetch(process.env.NEXT_PUBLIC_BACKEND_URL + '/api/todos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ title })
      });
      await response.json();
      setTitle('');
      fetchTodos();
    } catch (error) {
      setError('Failed to add todo');
    } finally {
      setLoading(false);
    }
  };

  const deleteTodo = async (id: string) => {
    setLoading(true);
    try {
      await fetch(process.env.NEXT_PUBLIC_BACKEND_URL + `/api/todos/${id}`, {
        method: 'DELETE'
      });
      fetchTodos();
    } catch (error) {
      setError('Failed to delete todo');
    } finally {
      setLoading(false);
    }
  };

  const toggleTodo = async (id: string, completed: boolean) => {
    setLoading(true);
    try {
      await fetch(process.env.NEXT_PUBLIC_BACKEND_URL + `/api/todos/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ completed: !completed })
      });
      fetchTodos();
    } catch (error) {
      setError('Failed to update todo');
    } finally {
      setLoading(false);
    }
  };

  return (
    <main className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-12 px-4">
      <div className="max-w-md mx-auto bg-white rounded-xl shadow-lg p-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-4">Todo App</h1>
        <div className="mb-4">
          <input 
            type="text" 
            className="border border-gray-300 rounded px-3 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Add new todo"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <button 
            onClick={addTodo} 
            className="mt-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-lg transition-colors duration-200"
            disabled={loading}
          >
            Add Todo
          </button>
        </div>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        {loading ? (
          <p>Loading...</p>
        ) : (
          <ul className="space-y-2">
            {todos.map((todo) => (
              <li key={todo._id} className="flex items-center justify-between bg-white p-4 rounded-lg shadow border">
                <span className={`${todo.completed ? 'line-through' : ''}`}>{todo.title}</span>
                <div className="space-x-2">
                  <button 
                    onClick={() => toggleTodo(todo._id, todo.completed)}
                    className="bg-green-500 hover:bg-green-600 text-white py-1 px-2 rounded"
                  >
                    {todo.completed ? 'Undo' : 'Complete'}
                  </button>
                  <button 
                    onClick={() => deleteTodo(todo._id)}
                    className="bg-red-500 hover:bg-red-600 text-white py-1 px-2 rounded"
                  >
                    Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </main>
  );
}"
