from fastapi.middleware.cors import CORSMiddleware
from pydantic import BaseModel
from pymongo import MongoClient
from bson import ObjectId

app = FastAPI()

app.add_middleware(
    CORSMiddleware,
    allow_origins=["*"],
    allow_credentials=True,
    allow_methods=["*"],
    allow_headers=["*"],
)

client = MongoClient('mongodb://localhost:27017/')
db = client.todoapp
todos_collection = db.todos

class TodoModel(BaseModel):
    title: str
    completed: bool = False

@app.get("/api/todos")
async def get_todos():
    todos = list(todos_collection.find())
    for todo in todos:
        todo["_id"] = str(todo["_id"])
    return todos

@app.post("/api/todos")
async def create_todo(todo: TodoModel):
    result = todos_collection.insert_one(todo.dict())
    return {"_id": str(result.inserted_id)}

@app.delete("/api/todos/{todo_id}")
async def delete_todo(todo_id: str):
    result = todos_collection.delete_one({"_id": ObjectId(todo_id)})
    if result.deleted_count == 0:
        raise HTTPException(status_code=404, detail="Todo not found")
    return {"message": "Todo deleted"}

@app.patch("/api/todos/{todo_id}")
async def update_todo(todo_id: str, todo: TodoModel):
    result = todos_collection.update_one({"_id": ObjectId(todo_id)}, {"$set": todo.dict()})
    if result.matched_count == 0:
        raise HTTPException(status_code=404, detail="Todo not found")
    return {"message": "Todo updated"}

@app.on_event("startup")
async def startup_event():
    try:
        client.admin.command('ping')
        print("MongoDB connection successful!")
    except Exception as e:
        print(f"MongoDB connection failed: {e}")

@app.get("/health")
def health_check():
    try:
        client.admin.command('ping')
        return {"status": "healthy", "database": "connected"}
    except Exception as e:
        return {"status": "unhealthy", "database": "disconnected", "error": str(e)}"
